import React from "react";
import styled from 'styled-components';
import { useState, useEffect } from "react";


const PicAdd = () => {
  const [button, setButton] = useState("비활성화");
  const [value, setValue] = useState("");

  const PicContainer = styled.div`
    background-color: #f2f2f2;
    width: 330px;
    height: 504px;
    display: flex;
    flex-direction: column;
    justify-content: flex-start;
    align-items: center;

  > .photoContainer {
    margin: 15px 0px;
    width: 300px;
    height: 300px;
    background-color: #404040;
  }

  `;

  const StoryWrapper = styled.input`
    margin: 15px 0px;
    width: 300px;
    height: 100px;
    background-color: #c2c2c2;


  `
  const onButton = () => {
    (button === "비활성화")? setButton("활성화") : setButton("비활성화");
  }
  const onChange = (event) => {
    setValue(event.target.value);
    event.target.focus
  }
  useEffect = (() => {
    setValue(value)
  },value)

    return (
        <div>
            <PicContainer>
                <div className="photoContainer"></div>
                <StoryWrapper type="text" onChange={onChange} value={value}></StoryWrapper>
                <button onClick={onButton}>{button}</button>
            </PicContainer>  
        </div>
)}
export default PicAdd;
