import React from "react";
import styled from 'styled-components';
import { useState } from "react";

const PicContainer = styled.div`
  background-color: #f2f2f2;
  width: 330px;
  height: 504px;
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  align-items: center;

  > .photoContainer {
    margin: 15px 0px;
    width: 300px;
    height: 300px;
    background-color: #404040;
  }

  > .storyContainer {
    margin: 15px 0px;
    width: 300px;
    height: 100px;
    background-color: #404040;
  }

`;

const StoryWrapper = styled.div`

`

const PicAdd = () => {
  const [button, setButton] = useState("비활성화");

  const onButton = () => {
    (button === "비활성화")? setButton("활성화") : setButton("비활성화");
  }

    return (
        <div>
            <PicContainer>
                <div className="photoContainer"></div>
                <div className="storyContainer"></div>
                <button onClick={onButton}>{button}</button>
            </PicContainer>  
        </div>
)}
export default PicAdd;
