import React from "react";
import styled from 'styled-components';
import { useState, useEffect, useRef } from "react";
import PicAddStory from "../components/PicAddStory";



const PicAdd = () => {
  const [button, setButton] = useState("활성화");
  const [readonly, setRO] = useState(true);
  const [text, setText] = useState("");
  // const storyRef = useRef(null)

  const PicContainer = styled.div`
    background-color: #f2f2f2;
    width: 330px;
    height: 504px;
    display: flex;
    flex-direction: column;
    justify-content: flex-start;
    align-items: center;

  > .photoContainer {
    margin: 15px 0px;
    width: 300px;
    height: 300px;
    background-color: #404040;
  }
  > .storyWrapper {
    margin: 15px 0px;
    width: 300px;
    height: 100px;
    background-color: #c2c2c2;
  }

  `;

  const StoryWrapper = styled.input`
    margin: 15px 0px;
    width: 300px;
    height: 100px;
    background-color: #c2c2c2;

  `
  const onButton = () => {
    if(button === "활성화"){
      setButton("비활성화");
      setRO(false);
    } else {
      setButton("활성화");
      setRO(true);
    }
  }

  const onChange = (event) => {
    setText(event.target.value);
  }

    return (
        <div>
            <PicContainer className="picContainer">
                <div className="photoContainer" ></div>
                {/* <StoryWrapper className="storyWrapper" onChange={onChange} type="text" value={text} readOnly={readonly} ref={storyRef}></StoryWrapper> */}
                {/* <input className="storyWrapper" type="text" value={text} onChange={onChange}></input> */}
                <PicAddStory></PicAddStory>
                <button onClick={onButton}>{button}</button>
            </PicContainer>  
        </div>
)}
export default PicAdd;
