import React, { useEffect, useState } from "react";
import styled from "styled-components";
import PicAddStory from "../components/PicAddStory";

const PicCategory = ({ category, categories, picData }) => {
  const [pic, SetPic] = useState({});

  const PicContainer = styled.div`
    background-color: #f2f2f2;
    width: 330px;
    height: 400px;
    margin: 10px;
    display: flex;
    flex-direction: column;
    justify-content: flex-start;
    align-items: center;

    > .photoContainer {
      margin: 15px 0px;
      width: 300px;
      height: 300px;
    }
    > .storyWrapper {
      margin: 15px 0px;
      width: 300px;
      height: 100px;
      background-color: #c2c2c2;
    }
  `;
  const Title = styled.input`
    margin: 15px 0px;
    width: 300px;
    height: 100px;
    background-color: #c2c2c2;
  `;
  useEffect(() => {
    let temp = {};
    picData.forEach((el) => {
      if (categories.includes(el.category)) {
        if (temp[el.category]) {
          temp[el.category].push(el.content[0].picture);
        } else {
          temp[el.category] = [el.content[0].picture];
        }
      }
    });
  }, []);

  return (
    <>
      <section></section>
      <div>
        <PicContainer className="picContainer">
          <div className="photoContainer">
            {/* <img src={picture} /> */}
            {/* <button onClick={onClick}>버튼</button> */}
          </div>
          <Title value={category}></Title>
        </PicContainer>
      </div>
    </>
  );
};
export default PicCategory;
