import React, { useState, useEffect } from "react";
import PicCategory from "./PicCategory";
import styled from "styled-components";

const Home = ({ picData }) => {
  const [categories, setCategories] = useState([]);
  const [categoryData, setCategoryData] = useState({});
  useEffect(() => {
    //임시로 카테고리 추가
    setCategories(["데이트", "김코딩과 함께한 날"]);
  }, []);

  // useEffect(() => {
  //   //카테고리데이터가 배열일 경우
  //   categories.forEach((el) => {
  //     categoryData.push([el, []]);
  //   });
  //   setCategoryData(categoryData);
  // }, [categories]);

  // useEffect(() => {
  //   // 카테고리데이터가 배열일 경우
  //   picData.forEach((el) => {
  //     el.content.forEach((content) => {
  //       if (categories.includes(content.category)) {
  //         categoryData[categories.indexOf(content.category)][1].push({
  //           date: el.date,
  //           picture: content.picture,
  //           text: content.text,
  //           title: el.title,
  //         });
  //       }
  //     });
  //   });
  //   setCategoryData(categoryData);
  // }, [categories]);

  useEffect(() => {
    //카테고리데이터가 객체일 경우
    categories.forEach((el) => {
      categoryData[el] = [];
    });
    setCategoryData(categoryData);
  }, [categories]);

  useEffect(() => {
    // 카테고리데이터가 객체일 경우
    picData.forEach((el) => {
      el.content.forEach((content) => {
        if (categories.includes(content.category)) {
          categoryData[content.category].push({
            date: el.date,
            picture: content.picture,
            text: content.text,
            title: el.title,
          });
        }
      });
    });
    setCategoryData(categoryData);
  }, [categories]);

  console.log(categoryData, "categoryData");
  console.log(categories, "categories");

  const CategoryContainer = styled.section`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
  `;

  class SimpleSlider extends Component {
    render() {
      const settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1,
      };
      const Wrap = styled.div``;

      return (
        <div>
          <h2> Single Item</h2>
          <Slider {...settings}>
            <Wrap>
              <h3>1</h3>
              <PicAdd num={1} />
            </Wrap>
            <Wrap>
              <h3>2</h3>
              <PicAdd num={2} />
            </Wrap>
            <div className="wrapper">
              <h3>3</h3>
            </div>
            <div>
              <h3>4</h3>
            </div>
            <div>
              <h3>5</h3>
            </div>
            <div>
              <h3>6</h3>
            </div>
          </Slider>
        </div>
      );
    }
  }
  return (
    <>
      <CategoryContainer>
        {categories.map((el, idx) => {
          return (
            <PicCategory key={idx} categoryData={categoryData} category={el} />
          );
        })}
      </CategoryContainer>
    </>
  );
};

export default Home;
