import React, { useState, useEffect } from "react";
import PicCategory from "./PicCategory";
import styled from "styled-components";

const Home = ({ picData }) => {
  const [categories, setCategories] = useState([]);
  const [categoryData, setCategoryData] = useState({});
  useEffect(() => {
    //임시로 카테고리 추가
    setCategories(["데이트", "김코딩과 함께한 날"]);
  }, []);

  useEffect(() => {
    categories.forEach((el) => {
      categoryData[el] = [];
    });
    setCategoryData(categoryData);
  }, [categories]);

  useEffect(() => {
    picData.forEach((el) => {
      el.content.forEach((content) => {
        if (categories.includes(content.category)) {
          categoryData[content.category].push({
            date: el.date,
            picture: content.picture,
            text: content.text,
            title: el.title,
          });
        }
      });
    });
    setCategoryData(categoryData);
  }, [categories]);

  console.log(categoryData, "categoryData");

  const CategoryContainer = styled.section`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
  `;
  return (
    <>
      {/* <CategoryContainer>
        {categories.map((el) => {
          return <PicCategory category={el} pic={pic} picData={picData} />;
        })}
      </CategoryContainer> */}
    </>
  );
};

export default Home;
