import React, { useEffect, useState, useRef } from "react";

const PicAddStory = ({ text, setText, setPicData }) => {
  const [button, setButton] = useState("활성화");
  const [readonly, setRO] = useState(true);
  const storyRef = useRef(null);

  const onButton = () => {
    if (button === "활성화") {
      setButton("비활성화");
      setRO(false);
    } else {
      setButton("활성화");
      setRO(true);
      setText(storyRef.current.value);
    }
  };

  const onMouseDown = () => {
    if (readonly === true)
      alert("글을 수정하려면 하단 버튼을 클릭하여서 수정모드로 변환해주세요.");
  };

  const onClick = () => {
    storyRef.current.value = "";
  };

  useEffect(() => {
    storyRef.current.value = text;
  });

  return (
    <>
      <input
        className="storyWrapper"
        type="text"
        ref={storyRef}
        readOnly={readonly}
        onMouseDown={onMouseDown}
      ></input>
      <button onClick={onButton}>{button}</button>
      {button === "비활성화" ? (
        <button onClick={onClick}>전체 내용 삭제하기</button>
      ) : null}
    </>
  );
};

export default PicAddStory;
