import React,{useState} from "react";

const PicAddStory = () => {
    const [text, setText] = useState("");
    const [button, setButton] = useState("활성화");
    const [readonly, setRO] = useState(true);

    const onChange = (event) => {
        setText(event.target.value);
    }

    const onButton = () => {
        if(button === "활성화"){
          setButton("비활성화");
          setRO(false);
        } else {
          setButton("활성화");
          setRO(true);
        }
    }

    return (
        <>
            <input className="storyWrapper" type="text" value={text} onChange={onChange} readOnly={readonly}></input>
            <button onClick={onButton}>{button}</button>
        </>
    )
}

export default PicAddStory
