import React from "react";
import styled from 'styled-components';
import { useState, useEffect, useRef } from "react";


const PicAdd = () => {
  const [button, setButton] = useState("활성화");
  const [value, setValue] = useState("");
  const [readonly, setRO] = useState(true);
  const stotyRef = useRef(null);

  const PicContainer = styled.div`
    background-color: #f2f2f2;
    width: 330px;
    height: 504px;
    display: flex;
    flex-direction: column;
    justify-content: flex-start;
    align-items: center;

  > .photoContainer {
    margin: 15px 0px;
    width: 300px;
    height: 300px;
    background-color: #404040;
  }

  `;

  const StoryWrapper = styled.input`
    margin: 15px 0px;
    width: 300px;
    height: 100px;
    background-color: #c2c2c2;


  `
  const onButton = () => {
    if(button === "활성화"){
      setButton("비활성화");
      stotyRef.current.readonly = "false";
      console.log(stotyRef.current)
    } else {
      setButton("활성화");
    }
  }
  const onChange = (event) => {
    console.log(event.target);
    setValue(event.target.value);
    event.target.focus();
  }

  useEffect = (() => {
    console.log(button)
    // if(button === "비활성화") {
    //   setRO(false);
    //   console.log(readonly);
    // } else {
    //   setRO(true);
    //   console.log(readonly);
    // }
  },[button])

  // useEffect (()=> {

  // },[readonly])

    return (
        <div>
            <PicContainer className="picContainer">
                <div className="photoContainer" ></div>
                <StoryWrapper className="storyWrapper" type="text" onChange={onChange} value={value} ref={stotyRef} readOnly={true} ></StoryWrapper>
                <button onClick={onButton}>{button}</button>
            </PicContainer>  
        </div>
)}
export default PicAdd;
