import React, { useState, useEffect, Component } from "react";
import styled from "styled-components";
import "../../node_modules/slick-carousel/slick/slick.css";
import "../../node_modules/slick-carousel/slick/slick-theme.css";
import Slider from "react-slick";

const PicCategory = ({ categoryData, category }) => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
  };
  const [storyCategory, setStoryCategory] = useState([]);
  const PicContainer = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    margin-bottom: 30px;
    > .category_title {
      display: flex;
      justify-content: flex-start;
      width: 210px;
      background-color: grey;
      margin: 10px;
      padding: 10px;
      vertical-align: middle;
      font-weight: bold;
    }
  `;
  const PicWrapper = styled.div`
    background-color: #f2f2f2;
    width: 330px;
    height: 400px;
    margin: 10px;
    display: flex;
    flex-direction: column;
    justify-content: flex-start;
    align-items: center;
    > .photoContainer {
      margin: 15px 0px;
      width: 300px;
      height: 300px;
    }
    > .storyWrapper {
      margin: 15px 0px;
      width: 300px;
      height: 40px;
      background-color: #c2c2c2;
      display: flex;
      flex-direction: column;
    }
  `;
  const Date = styled.span`
    width: 300px;
    height: 20px;
    background-color: #c2c2c2;
  `;
  const Content = styled.span`
    width: 300px;
    height: 20px;
    background-color: #c2c2c2;
  `;

  useEffect(() => {
    setStoryCategory(categoryData[category]);
  }, []);

  console.log(storyCategory, "storyCategory");

  const Wrap = styled.div`
    width: 300px;
    background-color: silver;
    height: 300px;
  `;

  class SimpleSlider extends Component {
    render() {
      const Wrap = styled.div``;
      console.log(storyCategory, "storyinsimple");

      return (
        <div>
          <h2> Single Item</h2>
          <Slider {...settings}>
            <Wrap>
              <h3>1</h3>
            </Wrap>
            <Wrap>
              <h3>2</h3>
            </Wrap>
            <div className="wrapper">
              <h3>3</h3>
            </div>
            <div>
              <h3>4</h3>
            </div>
            <div>
              <h3>5</h3>
            </div>
            <div>
              <h3>6</h3>
            </div>
          </Slider>
        </div>
      );
    }
  }
  return (
    <>
      <PicContainer>
        <span className="category_title">{category}</span>
        <Wrap>
          <SimpleSlider></SimpleSlider>
        </Wrap>
        {storyCategory.map((el) => {
          return (
            <>
              <PicWrapper>
                <div className="photoContainer">
                  <img src={el.picture} />
                </div>
                <div className="storyWrapper">
                  <Date>{el.date}</Date>
                  <Content>{`${el.title} / ${el.text}`}</Content>
                </div>
              </PicWrapper>
            </>
          );
        })}
      </PicContainer>
    </>
  );
};
export default PicCategory;
